// Simulate motor with gradual degradation
flow.cycle = (flow.cycle || 0) + 1;

// Base values with gradual degradation
var degradation = Math.min(flow.cycle * 0.02, 15); // Max 15 degree increase over time
var noise = (Math.random() - 0.5) * 2;

msg.payload = {
    temperature: 45 + degradation + noise,
    vibration: 2.5 + (degradation * 0.1) + (Math.random() - 0.5) * 0.5,
    power: 100 + (degradation * 2) + (Math.random() - 0.5) * 5
};

msg.timestamp = Date.now();
msg.motorId = "MOTOR-001";

// Reset after 500 cycles
if (flow.cycle > 500) flow.cycle = 0;

return msg;

// Simulate vibration with developing bearing fault
flow.t = (flow.t || 0) + 1;
flow.faultLevel = (flow.faultLevel || 0);

// Gradually increase fault level
if (flow.t % 1000 === 0) {
    flow.faultLevel = Math.min(flow.faultLevel + 0.1, 1.0);
}

var t = flow.t / 100;
var f1 = 30;   // Rotation frequency (30 Hz)
var f2 = 157;  // Bearing fault frequency (BPFO)

// Normal rotation signal
var signal = Math.sin(2 * Math.PI * f1 * t);

// Add bearing fault frequency with increasing amplitude
signal += flow.faultLevel * 0.5 * Math.sin(2 * Math.PI * f2 * t);

// Add random impacts (bearing defect)
if (Math.random() < 0.02 * flow.faultLevel) {
    signal += (Math.random() > 0.5 ? 3 : -3) * flow.faultLevel;
}

// Add noise
signal += (Math.random() - 0.5) * 0.3;

msg.payload = signal;
msg.timestamp = Date.now();

return msg;

// Simulate pump with gradual leak
flow.cycle = (flow.cycle || 0) + 1;

// Gradual leak reduces flow over time
var leak = Math.min(flow.cycle * 0.05, 20); // Max 20% flow loss
var noise = (Math.random() - 0.5) * 3;

msg.payload = {
    flow: 100 - leak + noise,
    pressure: 120 - (leak * 0.5) + (Math.random() - 0.5) * 5,
    temperature: 35 + (Math.random() - 0.5) * 2
};

// Occasional transient
if (Math.random() < 0.05) {
    msg.payload.temperature += 10;
}

msg.timestamp = Date.now();

// Reset after 400 cycles
if (flow.cycle > 400) flow.cycle = 0;

return msg;

// Simulate system with multiple operating modes
flow.cycle = (flow.cycle || 0) + 1;

// Determine current mode (changes every 50 samples)
var mode = Math.floor(flow.cycle / 50) % 3;

var value;
switch(mode) {
    case 0: // Low power mode
        value = 50 + (Math.random() - 0.5) * 10;
        break;
    case 1: // Medium power mode
        value = 75 + (Math.random() - 0.5) * 15;
        break;
    case 2: // High power mode (noisy)
        value = 100 + (Math.random() - 0.5) * 20;
        break;
}

// Insert random anomaly (2% chance)
if (Math.random() < 0.02) {
    value = value + (Math.random() > 0.5 ? 50 : -50);
    msg.isAnomalyInjected = true;
} else {
    msg.isAnomalyInjected = false;
}

msg.payload = value;
msg.mode = mode;
msg.timestamp = Date.now();

return msg;

// Simulate realistic vibration with optional defect
flow.t = (flow.t || 0) + 1;
flow.defectActive = flow.defectActive || false;

// After 1000 samples, activate defect
if (flow.t === 1000) {
    flow.defectActive = true;
    node.warn('‚ö†Ô∏è Bearing defect simulation started!');
}

var t = flow.t / 100;

// Base vibration (normal operation)
var signal = Math.sin(2 * Math.PI * 30 * t); // 30 Hz rotation
signal += 0.3 * Math.sin(2 * Math.PI * 60 * t); // 2x harmonic
signal += (Math.random() - 0.5) * 0.5; // noise

// Add defect signature
if (flow.defectActive) {
    // High frequency component (bearing fault)
    signal += 0.4 * Math.sin(2 * Math.PI * 157 * t);

    // Random impulses (impacts)
    if (Math.random() < 0.05) {
        signal += (Math.random() > 0.5 ? 3 : -3);
    }
}

msg.payload = signal;
msg.defectActive = flow.defectActive;

return msg;

// Interpret vibration features
var f = msg.payload;
var interpretation = [];

// Crest Factor interpretation
if (f.crestFactor > 6) {
    interpretation.push('HIGH Crest Factor: Impulsive behavior detected');
} else if (f.crestFactor > 4) {
    interpretation.push('ELEVATED Crest Factor: Monitor closely');
}

// Kurtosis interpretation
if (f.kurtosis > 3) {
    interpretation.push('HIGH Kurtosis: Sharp peaks indicate impacts');
}

// Health Score
if (f.healthScore < 70) {
    interpretation.push('LOW Health Score: Maintenance recommended');
}

msg.interpretation = interpretation;
msg.payload = {
    rms: f.rms.toFixed(3),
    crestFactor: f.crestFactor.toFixed(2),
    kurtosis: f.kurtosis.toFixed(2),
    healthScore: f.healthScore,
    issues: interpretation
};

if (interpretation.length > 0) {
    return [msg, null];
} else {
    return [null, msg];
}

// Simulate 5 sensor values (normalized 0-1)
// Temperature, Vibration, Pressure, Power, Speed
flow.cycle = (flow.cycle || 0) + 1;

// Normal operation values
var temp = 0.4 + (Math.random() - 0.5) * 0.1;      // ~0.4
var vibration = 0.3 + (Math.random() - 0.5) * 0.1; // ~0.3
var pressure = 0.5 + (Math.random() - 0.5) * 0.1;  // ~0.5
var power = 0.6 + (Math.random() - 0.5) * 0.1;     // ~0.6
var speed = 0.7 + (Math.random() - 0.5) * 0.1;     // ~0.7

// Inject anomaly every 20 cycles (5%)
if (flow.cycle % 20 === 0) {
    // Random anomaly pattern
    var anomalyType = Math.floor(Math.random() * 3);
    switch(anomalyType) {
        case 0: // Overheating
            temp = 0.9 + Math.random() * 0.1;
            vibration = 0.7 + Math.random() * 0.2;
            break;
        case 1: // Pressure drop
            pressure = 0.1 + Math.random() * 0.1;
            power = 0.9;
            break;
        case 2: // Vibration spike
            vibration = 0.85 + Math.random() * 0.15;
            speed = 0.4;
            break;
    }
    msg.isAnomalyInjected = true;
    msg.anomalyType = ['overheating', 'pressure_drop', 'vibration_spike'][anomalyType];
} else {
    msg.isAnomalyInjected = false;
}

// Model expects array of 5 normalized values
msg.payload = [temp, vibration, pressure, power, speed];
msg.sensorLabels = ['temperature', 'vibration', 'pressure', 'power', 'speed'];
msg.timestamp = Date.now();

return msg;

// Interpret the model output
var prediction = msg.prediction;
var probability = 0;

// Handle different output formats
if (Array.isArray(prediction)) {
    probability = prediction[0];
    if (Array.isArray(probability)) {
        probability = probability[0];
    }
} else if (typeof prediction === 'number') {
    probability = prediction;
} else if (prediction && prediction.data) {
    probability = prediction.data[0];
}

msg.anomalyProbability = probability;
msg.isAnomaly = probability > 0.5;
msg.confidence = Math.abs(probability - 0.5) * 2; // 0-1 confidence

msg.payload = {
    input: msg.payload,
    probability: probability,
    isAnomaly: msg.isAnomaly,
    confidence: (msg.confidence * 100).toFixed(1) + '%',
    wasInjected: msg.isAnomalyInjected
};

if (msg.isAnomaly) {
    return [msg, null];
} else {
    return [null, msg];
}

// Simulate 8x8 feature map for GOOD part
// The ONNX model expects 64 features (8x8)

var features = [];
for (var i = 0; i < 64; i++) {
    // Normal/Good: uniform features around 0.5
    features.push(0.5 + (Math.random() - 0.5) * 0.2);
}

msg.payload = features;
msg.expectedClass = 'good';
msg.timestamp = Date.now();

return msg;

// Simulate 8x8 feature map for SCRATCH defect
// Linear pattern in one row

var features = [];
for (var i = 0; i < 64; i++) {
    features.push(0.5 + (Math.random() - 0.5) * 0.2);
}

// Add scratch pattern (row 3)
for (var j = 24; j < 32; j++) {
    features[j] = 0.85 + Math.random() * 0.15;
}

msg.payload = features;
msg.expectedClass = 'scratch';
msg.timestamp = Date.now();

return msg;

// Interpret image classification output
var prediction = msg.prediction;

// Common class labels for defect detection
var classes = ['good', 'scratch', 'crack', 'corrosion', 'dent'];

// Handle different output formats
var probabilities = [];
if (Array.isArray(prediction)) {
    probabilities = Array.isArray(prediction[0]) ? prediction[0] : prediction;
} else if (prediction && prediction.data) {
    probabilities = Array.from(prediction.data);
}

// Find class with highest probability
var maxProb = 0;
var maxClass = 0;
for (var i = 0; i < probabilities.length; i++) {
    if (probabilities[i] > maxProb) {
        maxProb = probabilities[i];
        maxClass = i;
    }
}

var className = classes[maxClass] || 'class_' + maxClass;
var isDefect = maxClass > 0; // Class 0 = good

msg.payload = {
    predictedClass: className,
    confidence: (maxProb * 100).toFixed(1) + '%',
    isDefect: isDefect,
    allProbabilities: probabilities.map((p, i) => ({
        class: classes[i] || 'class_' + i,
        probability: (p * 100).toFixed(1) + '%'
    }))
};

if (isDefect) {
    return [msg, null];
} else {
    return [null, msg];
}

// Process uploaded image
// Expects multipart/form-data with 'image' field
// or JSON with base64 encoded image

if (msg.req.files && msg.req.files.image) {
    // File upload
    var file = msg.req.files.image[0];
    msg.imageBuffer = file.buffer;
    msg.filename = file.originalname;
    node.warn('Received file: ' + msg.filename);
} else if (msg.payload && msg.payload.image) {
    // Base64 encoded
    msg.imageBuffer = Buffer.from(msg.payload.image, 'base64');
    msg.filename = 'base64_image';
}

// In production: resize and normalize the image here
// For now, just pass through for demonstration
msg.payload = msg.imageBuffer;

return msg;

// Format API response
var prediction = msg.prediction || [];
var classes = ['good', 'scratch', 'crack', 'corrosion', 'dent'];

msg.payload = {
    success: true,
    filename: msg.filename,
    prediction: {
        class: classes[0], // Placeholder
        confidence: '95.2%',
        isDefect: false
    },
    timestamp: new Date().toISOString()
};

msg.headers = {'Content-Type': 'application/json'};

return msg;

// Interpret ONNX model output
var prediction = msg.prediction;
var probability = 0;

// Handle different output formats
if (Array.isArray(prediction)) {
    probability = prediction[0];
    if (Array.isArray(probability)) {
        probability = probability[0];
    }
} else if (typeof prediction === 'object' && prediction.output) {
    probability = prediction.output[0];
} else if (typeof prediction === 'number') {
    probability = prediction;
}

var isAnomaly = probability > 0.5;

msg.payload = {
    inputValues: msg.payload,
    anomalyProbability: Math.round(probability * 1000) / 10 + '%',
    isAnomaly: isAnomaly,
    status: isAnomaly ? '‚ö†Ô∏è ANOMALY DETECTED' : '‚úÖ Normal',
    confidence: isAnomaly 
        ? Math.round(probability * 100) + '%' 
        : Math.round((1 - probability) * 100) + '%',
    modelType: 'ONNX (PyTorch)',
    timestamp: new Date().toISOString()
};

return msg;

