        features[featureNames[idx]] = val.toFixed(3);
    });
}

msg.payload = {
    anomalyProbability: (probability * 100).toFixed(1) + '%',
    isAnomaly: isAnomaly,
    status: isAnomaly ? '‚ö†Ô∏è FAULT DETECTED' : '‚úÖ Normal',
    diagnosis: isAnomaly ? getDiagnosis(msg.payload) : 'Machine operating normally',
    features: features,
    modelType: 'PyTorch ‚Üí ONNX',
    timestamp: new Date().toISOString()
};

// Simple diagnosis based on feature patterns
function getDiagnosis(f) {
    if (!Array.isArray(f)) return 'Unknown fault';

    // High kurtosis + crest factor = bearing
    if (f[3] > 0.7 && f[2] > 0.7) return 'üî¥ Bearing fault suspected';

    // High RMS + low freq = imbalance
    if (f[0] > 0.7 && f[5] < 0.3) return 'üü† Rotor imbalance suspected';

    // Broadband + high crest = looseness
    if (f[6] > 0.5 && f[2] > 0.6) return 'üü° Mechanical looseness suspected';

    return '‚ö†Ô∏è Anomaly detected - check machine';
}

return msg;

